import{_ as e,c as a,o as r,aj as n}from"./chunks/framework.CtAXNf5e.js";const g=JSON.parse('{"title":"名词解释","description":"","frontmatter":{},"headers":[],"relativePath":"墙外指南/名词解释.md","filePath":"墙外指南/名词解释.md","lastUpdated":1768624460000}'),o={name:"墙外指南/名词解释.md"};function d(l,t,s,c,i,_){return r(),a("div",null,[...t[0]||(t[0]=[n('<h1 id="名词解释" tabindex="-1">名词解释 <a class="header-anchor" href="#名词解释" aria-label="Permalink to &quot;名词解释&quot;">​</a></h1><h2 id="系统代理和-tun-模式有什么区别" tabindex="-1">系统代理和 Tun 模式有什么区别？ <a class="header-anchor" href="#系统代理和-tun-模式有什么区别" aria-label="Permalink to &quot;系统代理和 Tun 模式有什么区别？&quot;">​</a></h2><p><strong>Tun 模式</strong>又称「<strong>虚拟网卡</strong>」。</p><blockquote><p>代理模式决定本机网络请求程序发出的流量如何抵达监听在本地的代理程序，即「<strong>入站</strong>」。</p></blockquote><table tabindex="0"><thead><tr><th style="text-align:center;">代理模式</th><th style="text-align:center;">说明</th><th style="text-align:center;">特点</th></tr></thead><tbody><tr><td style="text-align:center;">系统代理</td><td style="text-align:center;">1. 代理程序会在系统「约定」的特定位置（如注册表、系统变量等）设置好代理程序监听请求的端口信息，进行网络请求的应用会自发性地尝试读取这部分信息并将请求发送至代理程序，而不同操作系统的「约定」方式各异；<br>2. 系统代理更像是一种行业内的「约定」，并非所有程序都遵守这种非强制性的「约定」，最终采取哪种方式发生请求往往取决于开发人员的意愿。</td><td style="text-align:center;">1. 具有自发性，网络请求程序尝试使用「约定」配置或使用网络请求程序里额外指定的配置；<br>2. 不能代理 <code>UDP</code> 流量，如游戏数据包等。</td></tr><tr><td style="text-align:center;">TUN 模式（虚拟网卡）</td><td style="text-align:center;">代理程序会创建一张虚拟网卡通过配置操作系统的路由将网络请求重定向到这张虚拟网卡，代理程序从虚拟网卡中读取并处理这些网络请求。与系统代理不同的是，该步骤发生在网络请求程序发出请求之后，因此这种方法不依赖开发人员的意愿。</td><td style="text-align:center;">1. 拦截和处理所有流量（<code>TCP</code>/<code>UDP</code>）重定向到本地的代理程序；<br>2.网络请求程序无需额外配置。</td></tr></tbody></table>',5)])])}const p=e(o,[["render",d]]);export{g as __pageData,p as default};
